-- Table: Client.Client_Tbl
-- Data table for all Client Configurations
CREATE TABLE Client.Client_Tbl
(
	id			SERIAL,
	countryid	INT4 NOT NULL,	-- ID of the Country the Client can Operate in
	flowid		INT4 NOT NULL,	-- ID of the Flow the Customer has to go through in order to complete the Payment

	name		VARCHAR(50),
	username	VARCHAR(50),	-- GoMobile Username
	passwd		VARCHAR(50),	-- GoMobile Password

	logourl		VARCHAR(255),	-- Absolute URL where the mPoint can fetch the Client Logo
	cssurl		VARCHAR(255),	-- Absolute URL where the mPoint can fetch custom CSS file
	callbackurl	VARCHAR(255),	-- Absolute URL where mPoint should send payment status
	accepturl	VARCHAR(255),	-- Absolute URL where mPoint should direct the customer to upon accepted payment
	cancelurl	VARCHAR(255),	-- Absolute URL where mPoint should direct the customer to upon the customer cancelling the payment
	
	maxamount	INT4,						-- Maximum Amount an mPoint Transaction can be for the client
	lang		CHAR(2) DEFAULT 'gb',		-- Clients default language that all mPoint payment pages should use 
	smsrcpt		BOOL DEFAULT true,			-- Indication of whether mPoint should send out an SMS Receipt to the Customer
	emailrcpt	BOOL DEFAULT true,			-- Indication of whether mPoint should allow access to the E-Mail Receipt Component
	method		VARCHAR(6) DEFAULT 'mPoint', -- Method / Protocol used by mPoint when performing a Callback to the Client
	
	terms		TEXT,			-- Terms & Conditions for the Client
	mode		INT4 DEFAULT 0,	-- Client mode: 0 = Production, 1 = Test Mode with prefilled card Info, 2 = Certification Mode

	CONSTRAINT Client_PK PRIMARY KEY (id),
	CONSTRAINT Client2Country_FK FOREIGN KEY (countryid) REFERENCES System.Country_Tbl ON UPDATE CASCADE ON DELETE RESTRICT,
	CONSTRAINT Client2Flow_FK FOREIGN KEY (flowid) REFERENCES System.Flow_Tbl ON UPDATE CASCADE ON DELETE RESTRICT,
	CONSTRAINT Client_Chk CHECK (method = 'mPoint' OR method = 'PSP'),
	LIKE Template.General_Tbl INCLUDING DEFAULTS
) WITHOUT OIDS;

CREATE UNIQUE INDEX Client_UQ ON Client.Client_Tbl (Upper(name) );

